                          'ta': 'à®¤à®®à®¿à®´à¯',
                          'kn': 'à²•à²¨à³à²¨à²¡',
                          'gu': 'àª—à«àªœàª°àª¾àª¤à«€',
                          'bn': 'à¦¬à¦¾à¦‚à¦²à¦¾',
                          'or': 'à¬“à¬¡à¬¼à¬¿à¬†',
