import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Plus, Trash2, Image, FileText, MessageSquare, Link, Phone, Workflow } from "lucide-react";
import { cn } from "@/lib/utils";

interface TemplateCreationDialogProps {
  onCreateTemplate: (templateData: any) => void;
  children: React.ReactNode;
}

export default function TemplateCreationDialog({ onCreateTemplate, children }: TemplateCreationDialogProps) {
  const [open, setOpen] = useState(false);
  const [templateType, setTemplateType] = useState<'content' | 'image_content' | 'buttons' | 'multiple_buttons' | 'flow'>('content');
  const [formData, setFormData] = useState({
    name: '',
    category: 'marketing',
    language: 'en_US',
    headerType: 'none',
    headerText: '',
    headerImage: '',
    bodyText: '',
    footerText: '',
    buttons: [{ type: 'QUICK_REPLY', text: '' }],
    flowSteps: [{ title: '', description: '' }]
  });

  const [preview, setPreview] = useState(true);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const components: any[] = [];

    // Add header component if specified
    if (formData.headerType === 'text' && formData.headerText) {
      components.push({
        type: 'HEADER',
        format: 'TEXT',
        text: formData.headerText
      });
    } else if (formData.headerType === 'image' && formData.headerImage) {
      components.push({
        type: 'HEADER',
        format: 'IMAGE',
        example: {
          header_handle: [formData.headerImage]
        }
      });
    }

    // Add body component (required)
    components.push({
      type: 'BODY',
      text: formData.bodyText
    });

    // Add footer component if specified
    if (formData.footerText) {
      components.push({
        type: 'FOOTER',
        text: formData.footerText
      });
    }

    // Add buttons based on template type
    if (['buttons', 'multiple_buttons'].includes(templateType)) {
      const validButtons = formData.buttons.filter(btn => btn.text.trim());
      if (validButtons.length > 0) {
        components.push({
          type: 'BUTTONS',
          buttons: validButtons
        });
      }
    }

    // Add flow components for flow type
    if (templateType === 'flow') {
      const validSteps = formData.flowSteps.filter(step => step.title.trim());
      if (validSteps.length > 0) {
        components.push({
          type: 'FLOW',
          steps: validSteps
        });
      }
    }

    const templateData = {
      name: formData.name,
      category: formData.category,
      language: formData.language,
      components,
      status: 'pending'
    };

    onCreateTemplate(templateData);
    setOpen(false);

    // Reset form
    setFormData({
      name: '',
      category: 'marketing',
      language: 'en_US',
      headerType: 'none',
      headerText: '',
      headerImage: '',
      bodyText: '',
      footerText: '',
      buttons: [{ type: 'QUICK_REPLY', text: '' }],
      flowSteps: [{ title: '', description: '' }]
    });
  };

  const addButton = () => {
    setFormData(prev => ({
      ...prev,
      buttons: [...prev.buttons, { type: 'QUICK_REPLY', text: '' }]
    }));
  };

  const removeButton = (index: number) => {
    setFormData(prev => ({
      ...prev,
      buttons: prev.buttons.filter((_, i) => i !== index)
    }));
  };

  const updateButton = (index: number, field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      buttons: prev.buttons.map((btn, i) => 
        i === index ? { ...btn, [field]: value } : btn
      )
    }));
  };

  const addFlowStep = () => {
    setFormData(prev => ({
      ...prev,
      flowSteps: [...prev.flowSteps, { title: '', description: '' }]
    }));
  };

  const removeFlowStep = (index: number) => {
    setFormData(prev => ({
      ...prev,
      flowSteps: prev.flowSteps.filter((_, i) => i !== index)
    }));
  };

  const updateFlowStep = (index: number, field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      flowSteps: prev.flowSteps.map((step, i) => 
        i === index ? { ...step, [field]: value } : step
      )
    }));
  };

  const renderPreview = () => {
    const headerComponent = formData.headerType === 'text' && formData.headerText ? 
      { type: 'HEADER', format: 'TEXT', text: formData.headerText } :
      formData.headerType === 'image' && formData.headerImage ?
      { type: 'HEADER', format: 'IMAGE', example: { header_handle: [formData.headerImage] } } : null;

    return (
      <div className="bg-white rounded-lg border border-green-200 shadow-sm overflow-hidden">
        {/* Header Preview */}
        {headerComponent && headerComponent.format === 'IMAGE' && (
          <div className="w-full h-32 bg-gradient-to-r from-green-100 to-green-200 flex items-center justify-center">
            <div className="text-center text-green-700">
              <Image className="w-8 h-8 mx-auto mb-2" />
              <p className="text-xs">Header Image</p>
            </div>
          </div>
        )}

        {headerComponent && headerComponent.format === 'TEXT' && (
          <div className="px-4 py-2 bg-green-50 border-b border-green-200">
            <p className="font-semibold text-green-800 text-sm">{formData.headerText}</p>
          </div>
        )}

        {/* Body Preview */}
        <div className="p-4">
          <div className="flex items-center gap-2 mb-3">
            <i className="fas fa-certificate text-green-600 text-xs"></i>
            <span className="text-xs font-medium text-green-700 uppercase tracking-wide">
              Business Template
            </span>
          </div>
          <p className="text-sm text-slate-800 font-medium whitespace-pre-wrap leading-relaxed">
            {formData.bodyText || "Enter body text..."}
          </p>

          {/* Footer Preview */}
          {formData.footerText && (
            <div className="mt-3 pt-2 border-t border-slate-200">
              <p className="text-xs text-slate-500">{formData.footerText}</p>
            </div>
          )}
        </div>

        {/* Buttons Preview */}
        {(['buttons', 'multiple_buttons'].includes(templateType)) && formData.buttons.some(btn => btn.text.trim()) && (
          <div className="border-t border-green-200 bg-green-50 p-3">
            <div className="space-y-2">
              {formData.buttons.filter(btn => btn.text.trim()).map((button, index) => (
                <div key={index} className="flex items-center justify-between p-2 bg-white rounded border border-green-200">
                  <div className="flex items-center gap-2">
                    {button.type === 'URL' && <Link className="w-3 h-3 text-blue-600" />}
                    {button.type === 'PHONE_NUMBER' && <Phone className="w-3 h-3 text-green-600" />}
                    {button.type === 'QUICK_REPLY' && <MessageSquare className="w-3 h-3 text-green-600" />}
                    <span className="text-sm font-medium text-slate-700">{button.text}</span>
                  </div>
                  <span className="text-xs text-slate-400 uppercase">{button.type}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Flow Preview */}
        {templateType === 'flow' && formData.flowSteps.some(step => step.title.trim()) && (
          <div className="border-t border-green-200 bg-blue-50 p-3">
            <div className="flex items-center gap-2 mb-2">
              <Workflow className="w-4 h-4 text-blue-600" />
              <span className="text-sm font-medium text-blue-700">Interactive Flow</span>
            </div>
            <div className="space-y-2">
              {formData.flowSteps.filter(step => step.title.trim()).map((step, index) => (
                <div key={index} className="p-2 bg-white rounded border border-blue-200">
                  <p className="text-sm font-medium text-slate-700">{step.title}</p>
                  {step.description && (
                    <p className="text-xs text-slate-500 mt-1">{step.description}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-6xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Create WhatsApp Business Template</DialogTitle>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Form Section */}
          <div className="space-y-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              {/* Template Type Selection */}
              <div>
                <Label className="text-sm font-medium">Template Type</Label>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-2 mt-2">
                  {[
                    { value: 'content', label: 'Content Only', icon: FileText },
                    { value: 'image_content', label: 'Image + Content', icon: Image },
                    { value: 'buttons', label: 'Single Button', icon: MessageSquare },
                    { value: 'multiple_buttons', label: 'Multiple Buttons', icon: MessageSquare },
                    { value: 'flow', label: 'Interactive Flow', icon: Workflow }
                  ].map(({ value, label, icon: Icon }) => (
                    <button
                      key={value}
                      type="button"
                      onClick={() => setTemplateType(value as any)}
                      className={cn(
                        "flex items-center gap-2 p-3 rounded-lg border text-sm font-medium transition-colors",
                        templateType === value
                          ? "bg-green-50 border-green-300 text-green-700"
                          : "bg-white border-slate-200 text-slate-600 hover:bg-slate-50"
                      )}
                    >
                      <Icon className="w-4 h-4" />
                      {label}
                    </button>
                  ))}
                </div>
              </div>

              {/* Basic Template Info */}
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="name">Template Name</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="my_template_name"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="category">Category</Label>
                  <Select value={formData.category} onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="marketing">Marketing</SelectItem>
                      <SelectItem value="utility">Utility</SelectItem>
                      <SelectItem value="authentication">Authentication</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="language">Language</Label>
                  <Select value={formData.language} onValueChange={(value) => setFormData(prev => ({ ...prev, language: value }))}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="en_US">English (US)</SelectItem>
                      <SelectItem value="en">English</SelectItem>
                      <SelectItem value="te">Telugu</SelectItem>
                      <SelectItem value="hi">Hindi</SelectItem>
                      <SelectItem value="ta">Tamil</SelectItem>
                      <SelectItem value="kn">Kannada</SelectItem>
                      <SelectItem value="ml">Malayalam</SelectItem>
                      <SelectItem value="mr">Marathi</SelectItem>
                      <SelectItem value="gu">Gujarati</SelectItem>
                      <SelectItem value="bn">Bengali</SelectItem>
                      <SelectItem value="or">Odia</SelectItem>
                      <SelectItem value="pa">Punjabi</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              {/* Header Section */}
              {['image_content', 'buttons', 'multiple_buttons', 'flow'].includes(templateType) && (
                <div>
                  <Label>Header (Optional)</Label>
                  <Select value={formData.headerType} onValueChange={(value) => setFormData(prev => ({ ...prev, headerType: value }))}>
                    <SelectTrigger className="mt-2">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="none">No Header</SelectItem>
                      <SelectItem value="text">Text Header</SelectItem>
                      <SelectItem value="image">Image Header</SelectItem>
                    </SelectContent>
                  </Select>

                  {formData.headerType === 'text' && (
                    <Input
                      className="mt-2"
                      value={formData.headerText}
                      onChange={(e) => setFormData(prev => ({ ...prev, headerText: e.target.value }))}
                      placeholder="Header text"
                    />
                  )}

                  {formData.headerType === 'image' && (
                    <Input
                      className="mt-2"
                      value={formData.headerImage}
                      onChange={(e) => setFormData(prev => ({ ...prev, headerImage: e.target.value }))}
                      placeholder="Image URL"
                    />
                  )}
                </div>
              )}

              {/* Body Text */}
              <div>
                <Label htmlFor="bodyText">Message Body</Label>
                <Textarea
                  id="bodyText"
                  value={formData.bodyText}
                  onChange={(e) => setFormData(prev => ({ ...prev, bodyText: e.target.value }))}
                  placeholder="Enter your message content here..."
                  required
                  rows={4}
                />
              </div>

              {/* Footer */}
              <div>
                <Label htmlFor="footerText">Footer (Optional)</Label>
                <Input
                  id="footerText"
                  value={formData.footerText}
                  onChange={(e) => setFormData(prev => ({ ...prev, footerText: e.target.value }))}
                  placeholder="Footer text (optional)"
                />
              </div>

              {/* Buttons Section */}
              {['buttons', 'multiple_buttons'].includes(templateType) && (
                <div>
                  <div className="flex items-center justify-between">
                    <Label>Interactive Buttons</Label>
                    <Button type="button" onClick={addButton} size="sm" variant="outline">
                      <Plus className="w-4 h-4 mr-1" />
                      Add Button
                    </Button>
                  </div>
                  <div className="space-y-2 mt-2">
                    {formData.buttons.map((button, index) => (
                      <div key={index} className="flex gap-2">
                        <Select
                          value={button.type}
                          onValueChange={(value) => updateButton(index, 'type', value)}
                        >
                          <SelectTrigger className="w-32">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="QUICK_REPLY">Quick Reply</SelectItem>
                            <SelectItem value="URL">Website Link</SelectItem>
                            <SelectItem value="PHONE_NUMBER">Phone Call</SelectItem>
                          </SelectContent>
                        </Select>
                        <Input
                          value={button.text}
                          onChange={(e) => updateButton(index, 'text', e.target.value)}
                          placeholder="Button text"
                          className="flex-1"
                        />
                        {formData.buttons.length > 1 && (
                          <Button
                            type="button"
                            onClick={() => removeButton(index)}
                            size="sm"
                            variant="outline"
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Flow Steps Section */}
              {templateType === 'flow' && (
                <div>
                  <div className="flex items-center justify-between">
                    <Label>Flow Steps</Label>
                    <Button type="button" onClick={addFlowStep} size="sm" variant="outline">
                      <Plus className="w-4 h-4 mr-1" />
                      Add Step
                    </Button>
                  </div>
                  <div className="space-y-2 mt-2">
                    {formData.flowSteps.map((step, index) => (
                      <div key={index} className="flex gap-2">
                        <Input
                          value={step.title}
                          onChange={(e) => updateFlowStep(index, 'title', e.target.value)}
                          placeholder="Step title"
                          className="flex-1"
                        />
                        <Input
                          value={step.description}
                          onChange={(e) => updateFlowStep(index, 'description', e.target.value)}
                          placeholder="Description (optional)"
                          className="flex-1"
                        />
                        {formData.flowSteps.length > 1 && (
                          <Button
                            type="button"
                            onClick={() => removeFlowStep(index)}
                            size="sm"
                            variant="outline"
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="flex gap-2 pt-4">
                <Button type="submit" className="flex-1">
                  Create Template
                </Button>
                <Button type="button" variant="outline" onClick={() => setOpen(false)}>
                  Cancel
                </Button>
              </div>
            </form>
          </div>

          {/* Preview Section */}
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <h3 className="text-lg font-semibold">Preview</h3>
            </div>
            {renderPreview()}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

server/auth.ts.content:
import jwt from "jsonwebtoken";
import bcrypt from "bcrypt";
import { randomUUID } from "crypto";
import { storage } from "./storage";
import type { User, LoginCredentials } from "@shared/schema";

const JWT_SECRET = process.env.JWT_SECRET || "whatsapp-pro-secret-key-2025";
const SALT_ROUNDS = 10;

export interface AuthUser {
  id: string;
  username: string;
  name: string;
  role: string;
  email: string;
}

export interface LoginResult {
  success: boolean;
  token?: string;
  user?: AuthUser;
  message?: string;
}

export class AuthService {
  // Hash password
  async hashPassword(password: string): Promise<string> {
    return await bcrypt.hash(password, SALT_ROUNDS);
  }

  // Verify password
  async verifyPassword(password: string, hashedPassword: string): Promise<boolean> {
    return await bcrypt.compare(password, hashedPassword);
  }

  // Verify password by username (for current password verification)
  async verifyPasswordByUsername(username: string, password: string): Promise<boolean> {
    const user = await storage.getUserByUsername(username);
    if (!user) return false;
    return await bcrypt.compare(password, user.password);
  }

  // Generate JWT token
  generateToken(user: User): string {
    const payload = {
      id: user.id,
      username: user.username,
      role: user.role,
    };
    return jwt.sign(payload, JWT_SECRET, { expiresIn: "7d" });
  }

  // Verify JWT token
  verifyToken(token: string): any {
    try {
      return jwt.verify(token, JWT_SECRET);
    } catch (error) {
      return null;
    }
  }

  // Initialize default admin user
  async initializeDefaultUser(): Promise<void> {
    try {
      const existingAdmin = await storage.getUserByUsername("admin");

      if (!existingAdmin) {
        const hashedPassword = await this.hashPassword("admin123");
        await storage.createUser({
          username: "admin",
          password: hashedPassword,
          email: "admin@whatsapppro.com",
          name: "Administrator",
          role: "admin",
          isActive: true,
        });
        console.log("Default admin user created: username=admin, password=admin123");
      }
    } catch (error) {
      console.error("Error initializing default user:", error);
    }
  }

  // Login user
  async login(credentials: LoginCredentials): Promise<LoginResult> {
    try {
      const user = await storage.getUserByUsername(credentials.username);

      if (!user) {
        return {
          success: false,
          message: "Invalid username or password",
        };
      }

      if (!user.isActive) {
        return {
          success: false,
          message: "Account is deactivated. Please contact administrator.",
        };
      }

      const isPasswordValid = await this.verifyPassword(credentials.password, user.password);

      if (!isPasswordValid) {
        return {
          success: false,
          message: "Invalid username or password",
        };
      }

      // Update last login
      await storage.updateUser(user.id, {
        lastLogin: new Date(),
      });

      // Generate token
      const token = this.generateToken(user);

      // Create session
      await storage.createUserSession({
        userId: user.id,
        token,
        expiresAt: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
      });

      return {
        success: true,
        token,
        user: {
          id: user.id,
          username: user.username,
          name: user.name,
          role: user.role,
          email: user.email,
        },
      };
    } catch (error) {
      console.error("Login error:", error);
      return {
        success: false,
        message: "An error occurred during login",
      };
    }
  }

  // Get user from token
  async getUserFromToken(token: string): Promise<AuthUser | null> {
    try {
      const decoded = this.verifyToken(token);

      if (!decoded) {
        return null;
      }

      // Check if session exists and is active
      const session = await storage.getUserSession(token);
      if (!session || !session.isActive || session.expiresAt < new Date()) {
        return null;
      }

      const user = await storage.getUser(decoded.id);
      if (!user || !user.isActive) {
        return null;
      }

      return {
        id: user.id,
        username: user.username,
        name: user.name,
        role: user.role,
        email: user.email,
      };
    } catch (error) {
      console.error("Error getting user from token:", error);
      return null;
    }
  }

  // Logout user
  async logout(token: string): Promise<boolean> {
    try {
      return await storage.deleteUserSession(token);
    } catch (error) {
      console.error("Logout error:", error);
      return false;
    }
  }

  // Update user password
  async updateUserPassword(userId: string, newPassword: string): Promise<void> {
    const hashedPassword = await this.hashPassword(newPassword);
    await storage.updateUser(userId, { password: hashedPassword });
  }

  // Update user profile
  async updateUserProfile(userId: string, profileData: { name: string; email: string; username: string }): Promise<User | null> {
    const updatedUser = await storage.updateUser(userId, profileData);
    return updatedUser || null;
  }

  // Find user by username or email (for checking duplicates)
  async findUserByUsernameOrEmail(username: string, email: string): Promise<User | null> {
    try {
      const userByUsername = await storage.getUserByUsername(username);
      if (userByUsername) return userByUsername;

      // Check by email using getAllUsers since getUserByEmail doesn't exist
      const allUsers = await storage.getUsers();
      const userByEmail = allUsers.find(u => u.email === email);
      if (userByEmail) return userByEmail;

      return null;
    } catch (error) {
      return null;
    }
  }

  // Middleware to check authentication
  async authenticate(token: string): Promise<AuthUser | null> {
    if (!token) {
      return null;
    }

    // Remove "Bearer " prefix if present
    const cleanToken = token.startsWith("Bearer ") ? token.slice(7) : token;
    return await this.getUserFromToken(cleanToken);
  }
}

export const authService = new AuthService();

server/database-storage.ts.content:
import { 
  users, templates, messages, campaigns, contacts, settings, autoReplyRules, conversations, userSessions,
  type User, type InsertUser, type Template, type InsertTemplate, type Message, type InsertMessage, 
  type Campaign, type InsertCampaign, type Contact, type InsertContact, type Setting, type InsertSetting,
  type AutoReplyRule, type InsertAutoReplyRule, type Conversation, type InsertConversation,
  type UserSession, type InsertUserSession
} from "@shared/schema";
import { db } from "./db-mysql";
import { eq, desc, and, or, sql } from "drizzle-orm";
import type { IStorage } from "./storage";

export class DatabaseStorage implements IStorage {
  // Users & Authentication
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    await db.insert(users).values(insertUser);
    // MySQL doesn't support .returning(), so we query the created user
    const [user] = await db.select().from(users).where(eq(users.username, insertUser.username));
    if (!user) {
      throw new Error('Failed to create user');
    }
    return user;
  }

  async updateUser(id: string, userData: Partial<InsertUser>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({ ...userData, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // User Sessions
  async createUserSession(session: InsertUserSession): Promise<UserSession> {
    const [userSession] = await db.insert(userSessions).values(session).returning();
    return userSession;
  }

  async getUserSession(token: string): Promise<UserSession | undefined> {
    const [session] = await db
      .select()
      .from(userSessions)
      .where(and(eq(userSessions.token, token), eq(userSessions.isActive, true)));
    return session;
  }

  async deleteUserSession(token: string): Promise<boolean> {
    const result = await db
      .update(userSessions)
      .set({ isActive: false })
      .where(eq(userSessions.token, token));
    return result.rowCount > 0;
  }

  async deleteUserSessions(userId: string): Promise<boolean> {
    const result = await db
      .update(userSessions)
      .set({ isActive: false })
      .where(eq(userSessions.userId, userId));
    return result.rowCount > 0;
  }

  // Templates
  async getTemplates(): Promise<Template[]> {
    return await db.select().from(templates).orderBy(desc(templates.createdAt));
  }

  async getTemplate(id: string): Promise<Template | undefined> {
    const [template] = await db.select().from(templates).where(eq(templates.id, id));
    return template;
  }

  async createTemplate(template: InsertTemplate): Promise<Template> {
    const [newTemplate] = await db.insert(templates).values(template).returning();
    return newTemplate;
  }

  async updateTemplate(id: string, templateData: Partial<InsertTemplate>): Promise<Template | undefined> {
    const [template] = await db
      .update(templates)
      .set({ ...templateData, updatedAt: new Date() })
      .where(eq(templates.id, id))
      .returning();
    return template;
  }

  async deleteTemplate(id: string): Promise<boolean> {
    const result = await db.delete(templates).where(eq(templates.id, id));
    return result.rowCount > 0;
  }

  // Messages
  async getMessages(): Promise<Message[]> {
    return await db.select().from(messages).orderBy(desc(messages.createdAt));
  }

  async getMessage(id: string): Promise<Message | undefined> {
    const [message] = await db.select().from(messages).where(eq(messages.id, id));
    return message;
  }

  async createMessage(message: InsertMessage): Promise<Message> {
    const [newMessage] = await db.insert(messages).values(message).returning();

    // Update conversation
    if (message.conversationId) {
      await this.updateConversation(message.conversationId, {
        lastMessage: message.content,
        lastMessageAt: new Date(),
        unreadCount: message.direction === 'inbound' ? 1 : 0,
      });
    }

    return newMessage;
  }

  async updateMessage(id: string, messageData: Partial<InsertMessage>): Promise<Message | undefined> {
    const [message] = await db
      .update(messages)
      .set({ ...messageData, statusUpdatedAt: new Date() })
      .where(eq(messages.id, id))
      .returning();
    return message;
  }

  async getMessagesByPhoneNumber(phoneNumber: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(eq(messages.phoneNumber, phoneNumber))
      .orderBy(desc(messages.createdAt));
  }

  async getMessagesByConversation(conversationId: string): Promise<Message[]> {
    return await db
      .select()
      .from(messages)
      .where(eq(messages.conversationId, conversationId))
      .orderBy(desc(messages.createdAt));
  }

  // Conversations
  async getConversations(): Promise<Conversation[]> {
    return await db.select().from(conversations).orderBy(desc(conversations.lastMessageAt));
  }

  async getConversation(id: string): Promise<Conversation | undefined> {
    const [conversation] = await db.select().from(conversations).where(eq(conversations.id, id));
    return conversation;
  }

  async getConversationByPhoneNumber(phoneNumber: string): Promise<Conversation | undefined> {
    const [conversation] = await db
      .select()
      .from(conversations)
      .where(eq(conversations.phoneNumber, phoneNumber));
    return conversation;
  }

  async createConversation(conversation: InsertConversation): Promise<Conversation> {
    const [newConversation] = await db.insert(conversations).values(conversation).returning();
    return newConversation;
  }

  async updateConversation(id: string, conversationData: Partial<InsertConversation>): Promise<Conversation | undefined> {
    const [conversation] = await db
      .update(conversations)
      .set({ ...conversationData, updatedAt: new Date() })
      .where(eq(conversations.id, id))
      .returning();
    return conversation;
  }

  // Auto Reply Rules
  async getAutoReplyRules(): Promise<AutoReplyRule[]> {
    return await db.select().from(autoReplyRules).orderBy(desc(autoReplyRules.priority));
  }

  async getAutoReplyRule(id: string): Promise<AutoReplyRule | undefined> {
    const [rule] = await db.select().from(autoReplyRules).where(eq(autoReplyRules.id, id));
    return rule;
  }

  async createAutoReplyRule(rule: InsertAutoReplyRule): Promise<AutoReplyRule> {
    const [newRule] = await db.insert(autoReplyRules).values(rule).returning();
    return newRule;
  }

  async updateAutoReplyRule(id: string, ruleData: Partial<InsertAutoReplyRule>): Promise<AutoReplyRule | undefined> {
    const [rule] = await db
      .update(autoReplyRules)
      .set({ ...ruleData, updatedAt: new Date() })
      .where(eq(autoReplyRules.id, id))
      .returning();
    return rule;
  }

  async deleteAutoReplyRule(id: string): Promise<boolean> {
    const result = await db.delete(autoReplyRules).where(eq(autoReplyRules.id, id));
    return result.rowCount > 0;
  }

  async getActiveAutoReplyRules(): Promise<AutoReplyRule[]> {
    return await db
      .select()
      .from(autoReplyRules)
      .where(eq(autoReplyRules.isActive, true))
      .orderBy(desc(autoReplyRules.priority));
  }

  // Campaigns
  async getCampaigns(): Promise<Campaign[]> {
    return await db.select().from(campaigns).orderBy(desc(campaigns.createdAt));
  }

  async getCampaign(id: string): Promise<Campaign | undefined> {
    const [campaign] = await db.select().from(campaigns).where(eq(campaigns.id, id));
    return campaign;
  }

  async createCampaign(campaign: InsertCampaign): Promise<Campaign> {
    const [newCampaign] = await db.insert(campaigns).values(campaign).returning();
    return newCampaign;
  }

  async updateCampaign(id: string, campaignData: Partial<InsertCampaign>): Promise<Campaign | undefined> {
    const [campaign] = await db
      .update(campaigns)
      .set(campaignData)
      .where(eq(campaigns.id, id))
      .returning();
    return campaign;
  }

  // Contacts
  async getContacts(): Promise<Contact[]> {
    return await db.select().from(contacts).orderBy(desc(contacts.createdAt));
  }

  async getContact(id: string): Promise<Contact | undefined> {
    const [contact] = await db.select().from(contacts).where(eq(contacts.id, id));
    return contact;
  }

  async createContact(contact: InsertContact): Promise<Contact> {
    const [newContact] = await db.insert(contacts).values(contact).returning();
    return newContact;
  }

  async updateContact(id: string, contactData: Partial<InsertContact>): Promise<Contact | undefined> {
    const [contact] = await db
      .update(contacts)
      .set(contactData)
      .where(eq(contacts.id, id))
      .returning();
    return contact;
  }

  async deleteContact(id: string): Promise<boolean> {
    const result = await db.delete(contacts).where(eq(contacts.id, id));
    return result.rowCount > 0;
  }

  // Settings
  async getSettings(): Promise<Setting[]> {
    return await db.select().from(settings).orderBy(desc(settings.createdAt));
  }

  async getSetting(key: string): Promise<Setting | undefined> {
    const [setting] = await db.select().from(settings).where(eq(settings.key, key));
    return setting;
  }

  async setSetting(setting: InsertSetting): Promise<Setting> {
    try {
      // Check if setting exists
      const existingSetting = await this.getSetting(setting.key);

      if (existingSetting) {
        // Update existing setting
        const [updatedSetting] = await db
          .update(settings)
          .set({
            value: setting.value,
            category: setting.category || existingSetting.category,
            isEncrypted: setting.isEncrypted !== undefined ? setting.isEncrypted : existingSetting.isEncrypted,
            updatedAt: new Date(),
          })
          .where(eq(settings.key, setting.key))
          .returning();
        return updatedSetting;
      } else {
        // Create new setting
        const [newSetting] = await db.insert(settings).values(setting).returning();
        return newSetting;
      }
    } catch (error) {
      console.error('Settings update error:', error);
      throw error;
    }
  }

  async updateSetting(key: string, value: any): Promise<Setting | undefined> {
    const [setting] = await db
      .update(settings)
      .set({ value, updatedAt: new Date() })
      .where(eq(settings.key, key))
      .returning();
    return setting;
  }

  async deleteSetting(key: string): Promise<boolean> {
    const result = await db.delete(settings).where(eq(settings.key, key));
    return result.rowCount > 0;
  }
}

